// SRD 5e Spell Data - Additional Spells (A-L)
const SRDSpells2 = [
    // Cantrips (Level 0)
    { name: "Blade Ward", level: 0, school: "abjuration", castingTime: "1 action", range: "Self", components: "V, S", duration: "1 round", classes: ["Bard", "Sorcerer", "Warlock", "Wizard"], description: "You extend your hand and trace a sigil of warding in the air. Until the end of your next turn, you have resistance against bludgeoning, piercing, and slashing damage dealt by weapon attacks.", source: "SRD" },
    { name: "Druidcraft", level: 0, school: "transmutation", castingTime: "1 action", range: "30 feet", components: "V, S", duration: "Instantaneous", classes: ["Druid"], description: "Whispering to the spirits of nature, you create one of the following effects within range: You create a tiny, harmless sensory effect that predicts what the weather will be at your location for the next 24 hours. You instantly make a flower blossom, a seed pod open, or a leaf bud bloom. You create an instantaneous, harmless sensory effect, such as falling leaves, a puff of wind, the sound of a small animal, or the faint odor of skunk. You instantly light or snuff out a candle, a torch, or a small campfire.", source: "SRD" },
    { name: "Friends", level: 0, school: "enchantment", castingTime: "1 action", range: "Self", components: "S, M (a small amount of makeup applied to the face as this spell is cast)", duration: "Concentration, up to 1 minute", classes: ["Bard", "Sorcerer", "Warlock", "Wizard"], description: "For the duration, you have advantage on all Charisma checks directed at one creature of your choice that isn't hostile toward you. When the spell ends, the creature realizes that you used magic to influence its mood and becomes hostile toward you.", source: "SRD" },

    // 1st Level Spells
    { name: "Alarm", level: 1, school: "abjuration", ritual: true, castingTime: "1 minute", range: "30 feet", components: "V, S, M (a tiny bell and a piece of fine silver wire)", duration: "8 hours", classes: ["Ranger", "Wizard"], description: "You set an alarm against unwanted intrusion. Choose a door, a window, or an area within range that is no larger than a 20-foot cube. Until the spell ends, an alarm alerts you whenever a Tiny or larger creature touches or enters the warded area. When you cast the spell, you can designate creatures that won't set off the alarm. You also choose whether the alarm is mental or audible.", source: "SRD" },
    { name: "Animal Friendship", level: 1, school: "enchantment", castingTime: "1 action", range: "30 feet", components: "V, S, M (a morsel of food)", duration: "24 hours", classes: ["Bard", "Druid", "Ranger"], description: "This spell lets you convince a beast that you mean it no harm. Choose a beast that you can see within range. It must see and hear you. If the beast's Intelligence is 4 or higher, the spell fails. Otherwise, the beast must succeed on a Wisdom saving throw or be charmed by you for the spell's duration. If you or one of your companions harms the target, the spell ends.", source: "SRD" },
    { name: "Armor of Agathys", level: 1, school: "abjuration", castingTime: "1 action", range: "Self", components: "V, S, M (a cup of water)", duration: "1 hour", classes: ["Warlock"], description: "A protective magical force surrounds you, manifesting as a spectral frost that covers you and your gear. You gain 5 temporary hit points for the duration. If a creature hits you with a melee attack while you have these hit points, the creature takes 5 cold damage. At Higher Levels: Both the temporary hit points and the cold damage increase by 5 for each slot level above 1st.", source: "SRD" },
    { name: "Arms of Hadar", level: 1, school: "conjuration", castingTime: "1 action", range: "Self (10-foot radius)", components: "V, S", duration: "Instantaneous", classes: ["Warlock"], description: "You invoke the power of Hadar, the Dark Hunger. Tendrils of dark energy erupt from you and batter all creatures within 10 feet of you. Each creature in that area must make a Strength saving throw. On a failed save, a target takes 2d6 necrotic damage and can't take reactions until its next turn. On a successful save, the creature takes half damage, but suffers no other effect. At Higher Levels: The damage increases by 1d6 for each slot level above 1st.", source: "SRD" },
    { name: "Chromatic Orb", level: 1, school: "evocation", castingTime: "1 action", range: "90 feet", components: "V, S, M (a diamond worth at least 50 gp)", duration: "Instantaneous", classes: ["Sorcerer", "Wizard"], description: "You hurl a 4-inch-diameter sphere of energy at a creature that you can see within range. You choose acid, cold, fire, lightning, poison, or thunder for the type of orb you create, and then make a ranged spell attack against the target. On a hit, the creature takes 3d8 damage of the type you chose. At Higher Levels: The damage increases by 1d8 for each slot level above 1st.", source: "SRD" },
    { name: "Color Spray", level: 1, school: "illusion", castingTime: "1 action", range: "Self (15-foot cone)", components: "V, S, M (a pinch of powder or sand that is colored red, yellow, and blue)", duration: "1 round", classes: ["Sorcerer", "Wizard"], description: "A dazzling array of flashing, colored light springs from your hand. Roll 6d10; the total is how many hit points of creatures this spell can affect. Creatures in a 15-foot cone originating from you are affected in ascending order of their current hit points. Each creature affected by this spell is blinded until the end of your next turn. At Higher Levels: Roll an additional 2d10 for each slot level above 1st.", source: "SRD" },
    { name: "Command", level: 1, school: "enchantment", castingTime: "1 action", range: "60 feet", components: "V", duration: "1 round", classes: ["Cleric", "Paladin"], description: "You speak a one-word command to a creature you can see within range. The target must succeed on a Wisdom saving throw or follow the command on its next turn. The spell has no effect if the target is undead, if it doesn't understand your language, or if your command is directly harmful to it. Some typical commands include Approach, Drop, Flee, Grovel, and Halt.", source: "SRD" },
    { name: "Compelled Duel", level: 1, school: "enchantment", castingTime: "1 bonus action", range: "30 feet", components: "V", duration: "Concentration, up to 1 minute", classes: ["Paladin"], description: "You attempt to compel a creature into a duel. One creature that you can see within range must make a Wisdom saving throw. On a failed save, the creature is drawn to you, compelled by your divine demand. For the duration, it has disadvantage on attack rolls against creatures other than you, and must make a Wisdom saving throw each time it attempts to move to a space that is more than 30 feet away from you.", source: "SRD" },
    { name: "Comprehend Languages", level: 1, school: "divination", ritual: true, castingTime: "1 action", range: "Self", components: "V, S, M (a pinch of soot and salt)", duration: "1 hour", classes: ["Bard", "Sorcerer", "Warlock", "Wizard"], description: "For the duration, you understand the literal meaning of any spoken language that you hear. You also understand any written language that you see, but you must be touching the surface on which the words are written. It takes about 1 minute to read one page of text. This spell doesn't decode secret messages in a text or a glyph, such as an arcane sigil, that isn't part of a written language.", source: "SRD" },
    { name: "Create or Destroy Water", level: 1, school: "transmutation", castingTime: "1 action", range: "30 feet", components: "V, S, M (a drop of water if creating water or a few grains of sand if destroying it)", duration: "Instantaneous", classes: ["Cleric", "Druid"], description: "You either create or destroy water. Create Water: You create up to 10 gallons of clean water within range in an open container. Alternatively, the water falls as rain in a 30-foot cube within range. Destroy Water: You destroy up to 10 gallons of water in an open container within range. Alternatively, you destroy fog in a 30-foot cube within range. At Higher Levels: You create or destroy 10 additional gallons of water, or the size of the cube increases by 5 feet, for each slot level above 1st.", source: "SRD" },
    { name: "Detect Evil and Good", level: 1, school: "divination", castingTime: "1 action", range: "Self", components: "V, S", duration: "Concentration, up to 10 minutes", classes: ["Cleric", "Paladin"], description: "For the duration, you know if there is an aberration, celestial, elemental, fey, fiend, or undead within 30 feet of you, as well as where the creature is located. Similarly, you know if there is a place or object within 30 feet of you that has been magically consecrated or desecrated. The spell can penetrate most barriers, but it is blocked by 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt.", source: "SRD" },
    { name: "Detect Poison and Disease", level: 1, school: "divination", ritual: true, castingTime: "1 action", range: "Self", components: "V, S, M (a yew leaf)", duration: "Concentration, up to 10 minutes", classes: ["Cleric", "Druid", "Paladin", "Ranger"], description: "For the duration, you can sense the presence and location of poisons, poisonous creatures, and diseases within 30 feet of you. You also identify the kind of poison, poisonous creature, or disease in each case. The spell can penetrate most barriers, but it is blocked by 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt.", source: "SRD" },
    { name: "Dissonant Whispers", level: 1, school: "enchantment", castingTime: "1 action", range: "60 feet", components: "V", duration: "Instantaneous", classes: ["Bard"], description: "You whisper a discordant melody that only one creature of your choice within range can hear, wracking it with terrible pain. The target must make a Wisdom saving throw. On a failed save, it takes 3d6 psychic damage and must immediately use its reaction, if available, to move as far as its speed allows away from you. On a successful save, the target takes half as much damage and doesn't have to move away. At Higher Levels: The damage increases by 1d6 for each slot level above 1st.", source: "SRD" },
    { name: "Divine Favor", level: 1, school: "evocation", castingTime: "1 bonus action", range: "Self", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Paladin"], description: "Your prayer empowers you with divine radiance. Until the spell ends, your weapon attacks deal an extra 1d4 radiant damage on a hit.", source: "SRD" },
    { name: "Ensnaring Strike", level: 1, school: "conjuration", castingTime: "1 bonus action", range: "Self", components: "V", duration: "Concentration, up to 1 minute", classes: ["Ranger"], description: "The next time you hit a creature with a weapon attack before this spell ends, a writhing mass of thorny vines appears at the point of impact, and the target must succeed on a Strength saving throw or be restrained by the magical vines until the spell ends. A Large or larger creature has advantage on this saving throw. If the target succeeds on the save, the vines shrivel away. At Higher Levels: The damage increases by 1d6 for each slot level above 1st.", source: "SRD" },
    { name: "Entangle", level: 1, school: "conjuration", castingTime: "1 action", range: "90 feet", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Druid"], description: "Grasping weeds and vines sprout from the ground in a 20-foot square starting from a point within range. For the duration, these plants turn the ground in the area into difficult terrain. A creature in the area when you cast the spell must succeed on a Strength saving throw or be restrained by the entangling plants until the spell ends. A creature restrained by the plants can use its action to make a Strength check against your spell save DC. On a success, it frees itself.", source: "SRD" },
    { name: "Expeditious Retreat", level: 1, school: "transmutation", castingTime: "1 bonus action", range: "Self", components: "V, S", duration: "Concentration, up to 10 minutes", classes: ["Sorcerer", "Warlock", "Wizard"], description: "This spell allows you to move at an incredible pace. When you cast this spell, and then as a bonus action on each of your turns until the spell ends, you can take the Dash action.", source: "SRD" },
    { name: "Faerie Fire", level: 1, school: "evocation", castingTime: "1 action", range: "60 feet", components: "V", duration: "Concentration, up to 1 minute", classes: ["Bard", "Druid"], description: "Each object in a 20-foot cube within range is outlined in blue, green, or violet light (your choice). Any creature in the area when the spell is cast is also outlined in light if it fails a Dexterity saving throw. For the duration, objects and affected creatures shed dim light in a 10-foot radius. Any attack roll against an affected creature or object has advantage if the attacker can see it, and the affected creature or object can't benefit from being invisible.", source: "SRD" },
    { name: "False Life", level: 1, school: "necromancy", castingTime: "1 action", range: "Self", components: "V, S, M (a small amount of alcohol or distilled spirits)", duration: "1 hour", classes: ["Sorcerer", "Wizard"], description: "Bolstering yourself with a necromantic facsimile of life, you gain 1d4 + 4 temporary hit points for the duration. At Higher Levels: You gain 5 additional temporary hit points for each slot level above 1st.", source: "SRD" },
    { name: "Feather Fall", level: 1, school: "transmutation", castingTime: "1 reaction, which you take when you or a creature within 60 feet of you falls", range: "60 feet", components: "V, M (a small feather or piece of down)", duration: "1 minute", classes: ["Bard", "Sorcerer", "Wizard"], description: "Choose up to five falling creatures within range. A falling creature's rate of descent slows to 60 feet per round until the spell ends. If the creature lands before the spell ends, it takes no falling damage and can land on its feet, and the spell ends for that creature.", source: "SRD" },
    { name: "Find Familiar", level: 1, school: "conjuration", ritual: true, castingTime: "1 hour", range: "10 feet", components: "V, S, M (10 gp worth of charcoal, incense, and herbs that must be consumed by fire in a brass brazier)", duration: "Instantaneous", classes: ["Wizard"], description: "You gain the service of a familiar, a spirit that takes an animal form you choose: bat, cat, crab, frog (toad), hawk, lizard, octopus, owl, poisonous snake, fish (quipper), rat, raven, sea horse, spider, or weasel. The familiar can telepathically communicate with you and can be dismissed or resummoned.", source: "SRD" },
    { name: "Fog Cloud", level: 1, school: "conjuration", castingTime: "1 action", range: "120 feet", components: "V, S", duration: "Concentration, up to 1 hour", classes: ["Druid", "Ranger", "Sorcerer", "Wizard"], description: "You create a 20-foot-radius sphere of fog centered on a point within range. The sphere spreads around corners, and its area is heavily obscured. It lasts for the duration or until a wind of moderate or greater speed (at least 10 miles per hour) disperses it. At Higher Levels: The radius of the fog increases by 20 feet for each slot level above 1st.", source: "SRD" },
    { name: "Goodberry", level: 1, school: "transmutation", castingTime: "1 action", range: "Touch", components: "V, S, M (a sprig of mistletoe)", duration: "Instantaneous", classes: ["Druid", "Ranger"], description: "Up to ten berries appear in your hand and are infused with magic for the duration. A creature can use its action to eat one berry. Eating a berry restores 1 hit point, and the berry provides enough nourishment to sustain a creature for one day. The berries lose their potency if they have not been consumed within 24 hours of the casting of this spell.", source: "SRD" },
    { name: "Grease", level: 1, school: "conjuration", castingTime: "1 action", range: "60 feet", components: "V, S, M (a bit of pork rind or butter)", duration: "1 minute", classes: ["Wizard"], description: "Slick grease covers the ground in a 10-foot square centered on a point within range and turns it into difficult terrain for the duration. When the grease appears, each creature standing in its area must succeed on a Dexterity saving throw or fall prone. A creature that enters the area or ends its turn there must also succeed on a Dexterity saving throw or fall prone.", source: "SRD" },
    { name: "Guiding Bolt", level: 1, school: "evocation", castingTime: "1 action", range: "120 feet", components: "V, S", duration: "1 round", classes: ["Cleric"], description: "A flash of light streaks toward a creature of your choice within range. Make a ranged spell attack against the target. On a hit, the target takes 4d6 radiant damage, and the next attack roll made against this target before the end of your next turn has advantage, thanks to the mystical dim light glittering on the target until then. At Higher Levels: The damage increases by 1d6 for each slot level above 1st.", source: "SRD" },
    { name: "Hail of Thorns", level: 1, school: "conjuration", castingTime: "1 bonus action", range: "Self", components: "V", duration: "Concentration, up to 1 minute", classes: ["Ranger"], description: "The next time you hit a creature with a ranged weapon attack before the spell ends, this spell creates a rain of thorns that sprouts from your ranged weapon or ammunition. In addition to the normal effect of the attack, the target of the attack and each creature within 5 feet of it must make a Dexterity saving throw. A creature takes 1d10 piercing damage on a failed save, or half as much damage on a successful one. At Higher Levels: The damage increases by 1d10 for each slot level above 1st.", source: "SRD" },
    { name: "Hellish Rebuke", level: 1, school: "evocation", castingTime: "1 reaction, which you take in response to being damaged by a creature within 60 feet of you that you can see", range: "60 feet", components: "V, S", duration: "Instantaneous", classes: ["Warlock"], description: "You point your finger, and the creature that damaged you is momentarily surrounded by hellish flames. The creature must make a Dexterity saving throw. It takes 2d10 fire damage on a failed save, or half as much damage on a successful one. At Higher Levels: The damage increases by 1d10 for each slot level above 1st.", source: "SRD" },
    { name: "Heroism", level: 1, school: "enchantment", castingTime: "1 action", range: "Touch", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Bard", "Paladin"], description: "A willing creature you touch is imbued with bravery. Until the spell ends, the creature is immune to being frightened and gains temporary hit points equal to your spellcasting ability modifier at the start of each of its turns. When the spell ends, the target loses any remaining temporary hit points from this spell. At Higher Levels: You can target one additional creature for each slot level above 1st.", source: "SRD" },
    { name: "Hex", level: 1, school: "enchantment", castingTime: "1 bonus action", range: "90 feet", components: "V, S, M (the petrified eye of a newt)", duration: "Concentration, up to 1 hour", classes: ["Warlock"], description: "You place a curse on a creature that you can see within range. Until the spell ends, you deal an extra 1d6 necrotic damage to the target whenever you hit it with an attack. Also, choose one ability when you cast the spell. The target has disadvantage on ability checks made with the chosen ability. If the target drops to 0 hit points before this spell ends, you can use a bonus action on a subsequent turn of yours to curse a new creature. At Higher Levels: Duration increases at higher levels.", source: "SRD" },
    { name: "Hunter's Mark", level: 1, school: "divination", castingTime: "1 bonus action", range: "90 feet", components: "V", duration: "Concentration, up to 1 hour", classes: ["Ranger"], description: "You choose a creature you can see within range and mystically mark it as your quarry. Until the spell ends, you deal an extra 1d6 damage to the target whenever you hit it with a weapon attack, and you have advantage on any Wisdom (Perception) or Wisdom (Survival) check you make to find it. If the target drops to 0 hit points before this spell ends, you can use a bonus action on a subsequent turn of yours to mark a new creature. At Higher Levels: Duration increases at higher levels.", source: "SRD" },
    { name: "Identify", level: 1, school: "divination", ritual: true, castingTime: "1 minute", range: "Touch", components: "V, S, M (a pearl worth at least 100 gp and an owl feather)", duration: "Instantaneous", classes: ["Bard", "Wizard"], description: "You choose one object that you must touch throughout the casting of the spell. If it is a magic item or some other magic-imbued object, you learn its properties and how to use them, whether it requires attunement to use, and how many charges it has, if any. You learn whether any spells are affecting the item and what they are. If the item was created by a spell, you learn which spell created it.", source: "SRD" },
    { name: "Illusory Script", level: 1, school: "illusion", ritual: true, castingTime: "1 minute", range: "Touch", components: "S, M (a lead-based ink worth at least 10 gp, which the spell consumes)", duration: "10 days", classes: ["Bard", "Warlock", "Wizard"], description: "You write on parchment, paper, or some other suitable writing material and imbue it with a potent illusion that lasts for the duration. To you and any creatures you designate when you cast the spell, the writing appears normal. To all others, the writing appears as if it were written in an unknown or magical script that is unintelligible.", source: "SRD" },
    { name: "Inflict Wounds", level: 1, school: "necromancy", castingTime: "1 action", range: "Touch", components: "V, S", duration: "Instantaneous", classes: ["Cleric"], description: "Make a melee spell attack against a creature you can reach. On a hit, the target takes 3d10 necrotic damage. At Higher Levels: The damage increases by 1d10 for each slot level above 1st.", source: "SRD" },
    { name: "Jump", level: 1, school: "transmutation", castingTime: "1 action", range: "Touch", components: "V, S, M (a grasshopper's hind leg)", duration: "1 minute", classes: ["Druid", "Ranger", "Sorcerer", "Wizard"], description: "You touch a creature. The creature's jump distance is tripled until the spell ends.", source: "SRD" },
    { name: "Longstrider", level: 1, school: "transmutation", castingTime: "1 action", range: "Touch", components: "V, S, M (a pinch of dirt)", duration: "1 hour", classes: ["Bard", "Druid", "Ranger", "Wizard"], description: "You touch a creature. The target's speed increases by 10 feet until the spell ends. At Higher Levels: You can target one additional creature for each slot level above 1st.", source: "SRD" },

    // 2nd Level Spells
    { name: "Aid", level: 2, school: "abjuration", castingTime: "1 action", range: "30 feet", components: "V, S, M (a tiny strip of white cloth)", duration: "8 hours", classes: ["Cleric", "Paladin"], description: "Your spell bolsters your allies with toughness and resolve. Choose up to three creatures within range. Each target's hit point maximum and current hit points increase by 5 for the duration. At Higher Levels: A target's hit points increase by an additional 5 for each slot level above 2nd.", source: "SRD" },
    { name: "Alter Self", level: 2, school: "transmutation", castingTime: "1 action", range: "Self", components: "V, S", duration: "Concentration, up to 1 hour", classes: ["Sorcerer", "Wizard"], description: "You assume a different form. When you cast the spell, choose one of the following options: Aquatic Adaptation (you adapt your body to an aquatic environment), Change Appearance (you transform your appearance), or Natural Weapons (you grow claws, fangs, spines, horns, or a different natural weapon of your choice that deals 1d6 bludgeoning, piercing, or slashing damage).", source: "SRD" },
    { name: "Animal Messenger", level: 2, school: "enchantment", ritual: true, castingTime: "1 action", range: "30 feet", components: "V, S, M (a morsel of food)", duration: "24 hours", classes: ["Bard", "Druid", "Ranger"], description: "By means of this spell, you use an animal to deliver a message. Choose a Tiny beast you can see within range, such as a squirrel, a blue jay, or a bat. You specify a location and a recipient who matches a general description. You also speak a message of up to twenty-five words. The target beast travels for the duration of the spell toward the specified location.", source: "SRD" },
    { name: "Arcane Lock", level: 2, school: "abjuration", castingTime: "1 action", range: "Touch", components: "V, S, M (gold dust worth at least 25 gp, which the spell consumes)", duration: "Until dispelled", classes: ["Wizard"], description: "You touch a closed door, window, gate, chest, or other entryway, and it becomes locked for the duration. You and the creatures you designate when you cast this spell can open the object normally. You can also set a password that, when spoken within 5 feet of the object, suppresses this spell for 1 minute. Otherwise, it is impassable until it is broken or the spell is dispelled or suppressed.", source: "SRD" },
    { name: "Augury", level: 2, school: "divination", ritual: true, castingTime: "1 minute", range: "Self", components: "V, S, M (specially marked sticks, bones, or similar tokens worth at least 25 gp)", duration: "Instantaneous", classes: ["Cleric"], description: "By casting gem-inlaid sticks, rolling dragon bones, laying out ornate cards, or employing some other divining tool, you receive an omen from an otherworldly entity about the results of a specific course of action that you plan to take within the next 30 minutes. The DM chooses from the following possible omens: Weal (good results), Woe (bad results), Weal and Woe (both), or Nothing (results that aren't especially good or bad).", source: "SRD" },
    { name: "Barkskin", level: 2, school: "transmutation", castingTime: "1 action", range: "Touch", components: "V, S, M (a handful of oak bark)", duration: "Concentration, up to 1 hour", classes: ["Druid", "Ranger"], description: "You touch a willing creature. Until the spell ends, the target's skin has a rough, bark-like appearance, and the target's AC can't be less than 16, regardless of what kind of armor it is wearing.", source: "SRD" },
    { name: "Beast Sense", level: 2, school: "divination", ritual: true, castingTime: "1 action", range: "Touch", components: "S", duration: "Concentration, up to 1 hour", classes: ["Druid", "Ranger"], description: "You touch a willing beast. For the duration of the spell, you can use your action to see through the beast's eyes and hear what it hears, and continue to do so until you use your action to return to your normal senses. While perceiving through the beast's senses, you gain the benefits of any special senses possessed by that creature, though you are blinded and deafened to your own surroundings.", source: "SRD" },
    { name: "Blindness/Deafness", level: 2, school: "necromancy", castingTime: "1 action", range: "30 feet", components: "V", duration: "1 minute", classes: ["Bard", "Cleric", "Sorcerer", "Wizard"], description: "You can blind or deafen a foe. Choose one creature that you can see within range to make a Constitution saving throw. If it fails, the target is either blinded or deafened (your choice) for the duration. At the end of each of its turns, the target can make a Constitution saving throw. On a success, the spell ends. At Higher Levels: You can target one additional creature for each slot level above 2nd.", source: "SRD" },
    { name: "Calm Emotions", level: 2, school: "enchantment", castingTime: "1 action", range: "60 feet", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Bard", "Cleric"], description: "You attempt to suppress strong emotions in a group of people. Each humanoid in a 20-foot-radius sphere centered on a point you choose within range must make a Charisma saving throw; a creature can choose to fail this saving throw if it wishes. If a creature fails its saving throw, choose one of the following two effects: suppress any effect causing a target to be charmed or frightened, or make a target indifferent about creatures of your choice that it is hostile toward.", source: "SRD" },
    { name: "Cloud of Daggers", level: 2, school: "conjuration", castingTime: "1 action", range: "60 feet", components: "V, S, M (a sliver of glass)", duration: "Concentration, up to 1 minute", classes: ["Bard", "Sorcerer", "Warlock", "Wizard"], description: "You fill the air with spinning daggers in a cube 5 feet on each side, centered on a point you choose within range. A creature takes 4d4 slashing damage when it enters the spell's area for the first time on a turn or starts its turn there. At Higher Levels: The damage increases by 2d4 for each slot level above 2nd.", source: "SRD" },
    { name: "Continual Flame", level: 2, school: "evocation", castingTime: "1 action", range: "Touch", components: "V, S, M (ruby dust worth 50 gp, which the spell consumes)", duration: "Until dispelled", classes: ["Cleric", "Wizard"], description: "A flame, equivalent in brightness to a torch, springs forth from an object that you touch. The effect looks like a regular flame, but it creates no heat and doesn't use oxygen. A continual flame can be covered or hidden but not smothered or quenched.", source: "SRD" },
    { name: "Cordon of Arrows", level: 2, school: "transmutation", castingTime: "1 action", range: "5 feet", components: "V, S, M (four or more arrows or bolts)", duration: "8 hours", classes: ["Ranger"], description: "You plant four pieces of nonmagical ammunition in the ground within range and lay magic upon them to protect an area. Until the spell ends, whenever a creature other than you comes within 30 feet of the ammunition for the first time on a turn or ends its turn there, one piece of ammunition flies up to strike it. The creature must succeed on a Dexterity saving throw or take 1d6 piercing damage.", source: "SRD" },
    { name: "Crown of Madness", level: 2, school: "enchantment", castingTime: "1 action", range: "120 feet", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Bard", "Sorcerer", "Warlock", "Wizard"], description: "One humanoid of your choice that you can see within range must succeed on a Wisdom saving throw or become charmed by you for the duration. While the target is charmed in this way, a twisted crown of jagged iron appears on its head, and a madness glows in its eyes. The charmed target must use its action before moving on each of its turns to make a melee attack against a creature other than itself that you mentally choose.", source: "SRD" },
    { name: "Darkvision", level: 2, school: "transmutation", castingTime: "1 action", range: "Touch", components: "V, S, M (either a pinch of dried carrot or an agate)", duration: "8 hours", classes: ["Druid", "Ranger", "Sorcerer", "Wizard"], description: "You touch a willing creature to grant it the ability to see in the dark. For the duration, that creature has darkvision out to a range of 60 feet.", source: "SRD" },
    { name: "Detect Thoughts", level: 2, school: "divination", castingTime: "1 action", range: "Self", components: "V, S, M (a copper piece)", duration: "Concentration, up to 1 minute", classes: ["Bard", "Sorcerer", "Wizard"], description: "For the duration, you can read the thoughts of certain creatures. When you cast the spell and as your action on each turn until the spell ends, you can focus your mind on any one creature that you can see within 30 feet of you. If the creature you choose has an Intelligence of 3 or lower or doesn't speak any language, the creature is unaffected.", source: "SRD" },
    { name: "Enlarge/Reduce", level: 2, school: "transmutation", castingTime: "1 action", range: "30 feet", components: "V, S, M (a pinch of powdered iron)", duration: "Concentration, up to 1 minute", classes: ["Sorcerer", "Wizard"], description: "You cause a creature or an object you can see within range to grow larger or smaller for the duration. Choose either a creature or an object that is neither worn nor carried. If the target is unwilling, it can make a Constitution saving throw. On a success, the spell has no effect. Enlarge: The target's size doubles and its weight multiplies by eight, granting advantage on Strength checks and +1d4 damage. Reduce: The target's size is halved and its weight is reduced to one-eighth, with disadvantage on Strength checks and -1d4 damage.", source: "SRD" },
    { name: "Enthrall", level: 2, school: "enchantment", castingTime: "1 action", range: "60 feet", components: "V, S", duration: "1 minute", classes: ["Bard", "Warlock"], description: "You weave a distracting string of words, causing creatures of your choice that you can see within range and that can hear you to make a Wisdom saving throw. Any creature that can't be charmed succeeds on this saving throw automatically, and if you or your companions are fighting a creature, it has advantage on the save. On a failed save, the target has disadvantage on Wisdom (Perception) checks made to perceive any creature other than you until the spell ends or until the target can no longer hear you.", source: "SRD" },
    { name: "Find Steed", level: 2, school: "conjuration", castingTime: "10 minutes", range: "30 feet", components: "V, S", duration: "Instantaneous", classes: ["Paladin"], description: "You summon a spirit that assumes the form of an unusually intelligent, strong, and loyal steed, creating a long-lasting bond with it. Appearing in an unoccupied space within range, the steed takes on a form that you choose: a warhorse, a pony, a camel, an elk, or a mastiff. The steed has the statistics of the chosen form, though it is a celestial, fey, or fiend (your choice) instead of its normal type.", source: "SRD" },
    { name: "Find Traps", level: 2, school: "divination", castingTime: "1 action", range: "120 feet", components: "V, S", duration: "Instantaneous", classes: ["Cleric", "Druid", "Ranger"], description: "You sense the presence of any trap within range that is within line of sight. A trap, for the purpose of this spell, includes anything that would inflict a sudden or unexpected effect you consider harmful or undesirable, which was specifically intended as such by its creator. This spell merely reveals that a trap is present. You don't learn the location of each trap, but you do learn the general nature of the danger posed by a trap you sense.", source: "SRD" },
    { name: "Flame Blade", level: 2, school: "evocation", castingTime: "1 bonus action", range: "Self", components: "V, S, M (leaf of sumac)", duration: "Concentration, up to 10 minutes", classes: ["Druid"], description: "You evoke a fiery blade in your free hand. The blade is similar in size and shape to a scimitar, and it lasts for the duration. If you let go of the blade, it disappears, but you can evoke the blade again as a bonus action. You can use your action to make a melee spell attack with the fiery blade. On a hit, the target takes 3d6 fire damage. At Higher Levels: The damage increases by 1d6 for every two slot levels above 2nd.", source: "SRD" },
    { name: "Flaming Sphere", level: 2, school: "conjuration", castingTime: "1 action", range: "60 feet", components: "V, S, M (a bit of tallow, a pinch of brimstone, and a dusting of powdered iron)", duration: "Concentration, up to 1 minute", classes: ["Druid", "Wizard"], description: "A 5-foot-diameter sphere of fire appears in an unoccupied space of your choice within range and lasts for the duration. Any creature that ends its turn within 5 feet of the sphere must make a Dexterity saving throw. The creature takes 2d6 fire damage on a failed save, or half as much damage on a successful one. As a bonus action, you can move the sphere up to 30 feet. At Higher Levels: The damage increases by 1d6 for each slot level above 2nd.", source: "SRD" },
    { name: "Gentle Repose", level: 2, school: "necromancy", ritual: true, castingTime: "1 action", range: "Touch", components: "V, S, M (a pinch of salt and one copper piece placed on each of the corpse's eyes, which must remain there for the duration)", duration: "10 days", classes: ["Cleric", "Wizard"], description: "You touch a corpse or other remains. For the duration, the target is protected from decay and can't become undead. The spell also effectively extends the time limit on raising the target from the dead, since days spent under the influence of this spell don't count against the time limit of spells such as raise dead.", source: "SRD" },
    { name: "Gust of Wind", level: 2, school: "evocation", castingTime: "1 action", range: "Self (60-foot line)", components: "V, S, M (a legume seed)", duration: "Concentration, up to 1 minute", classes: ["Druid", "Sorcerer", "Wizard"], description: "A line of strong wind 60 feet long and 10 feet wide blasts from you in a direction you choose for the spell's duration. Each creature that starts its turn in the line must succeed on a Strength saving throw or be pushed 15 feet away from you in a direction following the line. Any creature in the line must spend 2 feet of movement for every 1 foot it moves when moving closer to you.", source: "SRD" },
    { name: "Heat Metal", level: 2, school: "transmutation", castingTime: "1 action", range: "60 feet", components: "V, S, M (a piece of iron and a flame)", duration: "Concentration, up to 1 minute", classes: ["Bard", "Druid"], description: "Choose a manufactured metal object, such as a metal weapon or a suit of heavy or medium metal armor, that you can see within range. You cause the object to glow red-hot. Any creature in physical contact with the object takes 2d8 fire damage when you cast the spell. Until the spell ends, you can use a bonus action on each of your subsequent turns to cause this damage again. At Higher Levels: The damage increases by 1d8 for each slot level above 2nd.", source: "SRD" },
    { name: "Knock", level: 2, school: "transmutation", castingTime: "1 action", range: "60 feet", components: "V", duration: "Instantaneous", classes: ["Bard", "Sorcerer", "Wizard"], description: "Choose an object that you can see within range. The object can be a door, a box, a chest, a set of manacles, a padlock, or another object that contains a mundane or magical means that prevents access. A target that is held shut by a mundane lock or that is stuck or barred becomes unlocked, unstuck, or unbarred. If the object has multiple locks, only one of them is unlocked. If you choose a target that is held shut with arcane lock, that spell is suppressed for 10 minutes.", source: "SRD" },
    { name: "Lesser Restoration", level: 2, school: "abjuration", castingTime: "1 action", range: "Touch", components: "V, S", duration: "Instantaneous", classes: ["Bard", "Cleric", "Druid", "Paladin", "Ranger"], description: "You touch a creature and can end either one disease or one condition afflicting it. The condition can be blinded, deafened, paralyzed, or poisoned.", source: "SRD" },
    { name: "Levitate", level: 2, school: "transmutation", castingTime: "1 action", range: "60 feet", components: "V, S, M (either a small leather loop or a piece of golden wire bent into a cup shape with a long shank on one end)", duration: "Concentration, up to 10 minutes", classes: ["Sorcerer", "Wizard"], description: "One creature or loose object of your choice that you can see within range rises vertically, up to 20 feet, and remains suspended there for the duration. The spell can levitate a target that weighs up to 500 pounds. An unwilling creature that succeeds on a Constitution saving throw is unaffected. The target can move only by pushing or pulling against a fixed object or surface within reach.", source: "SRD" },
    { name: "Locate Animals or Plants", level: 2, school: "divination", ritual: true, castingTime: "1 action", range: "Self", components: "V, S, M (a bit of fur from a bloodhound)", duration: "Instantaneous", classes: ["Bard", "Druid", "Ranger"], description: "Describe or name a specific kind of beast or plant. Concentrating on the voice of nature in your surroundings, you learn the direction and distance to the closest creature or plant of that kind within 5 miles, if any are present.", source: "SRD" },
    { name: "Locate Object", level: 2, school: "divination", castingTime: "1 action", range: "Self", components: "V, S, M (a forked twig)", duration: "Concentration, up to 10 minutes", classes: ["Bard", "Cleric", "Druid", "Paladin", "Ranger", "Wizard"], description: "Describe or name an object that is familiar to you. You sense the direction to the object's location, as long as that object is within 1,000 feet of you. If the object is in motion, you know the direction of its movement. The spell can locate a specific object known to you, as long as you have seen it up close within 30 feet at least once.", source: "SRD" },
    { name: "Acid Arrow", level: 2, school: "evocation", castingTime: "1 action", range: "90 feet", components: "V, S, M (powdered rhubarb leaf and an adder's stomach)", duration: "Instantaneous", classes: ["Wizard"], description: "A shimmering green arrow streaks toward a target within range and bursts in a spray of acid. Make a ranged spell attack against the target. On a hit, the target takes 4d4 acid damage immediately and 2d4 acid damage at the end of its next turn. On a miss, the arrow splashes the target with acid for half as much of the initial damage and no damage at the end of its next turn. At Higher Levels: The damage (both initial and later) increases by 1d4 for each slot level above 2nd.", source: "SRD" },

    // 3rd Level Spells
    { name: "Animate Dead", level: 3, school: "necromancy", castingTime: "1 minute", range: "10 feet", components: "V, S, M (a drop of blood, a piece of flesh, and a pinch of bone dust)", duration: "Instantaneous", classes: ["Cleric", "Wizard"], description: "This spell creates an undead servant. Choose a pile of bones or a corpse of a Medium or Small humanoid within range. Your spell imbues the target with a foul mimicry of life, raising it as an undead creature. The target becomes a skeleton if you chose bones or a zombie if you chose a corpse. On each of your turns, you can use a bonus action to mentally command any creature you made with this spell if the creature is within 60 feet of you.", source: "SRD" },
    { name: "Beacon of Hope", level: 3, school: "abjuration", castingTime: "1 action", range: "30 feet", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Cleric"], description: "This spell bestows hope and vitality. Choose any number of creatures within range. For the duration, each target has advantage on Wisdom saving throws and death saving throws, and regains the maximum number of hit points possible from any healing.", source: "SRD" },
    { name: "Bestow Curse", level: 3, school: "necromancy", castingTime: "1 action", range: "Touch", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Bard", "Cleric", "Wizard"], description: "You touch a creature, and that creature must succeed on a Wisdom saving throw or become cursed for the duration of the spell. When you cast this spell, choose the nature of the curse from the following options: disadvantage on ability checks and saving throws with one ability score, disadvantage on attack rolls against you, Wisdom save each turn or waste action, or your attacks deal extra 1d8 necrotic damage. At Higher Levels: Duration increases and may become permanent at 9th level.", source: "SRD" },
    { name: "Blink", level: 3, school: "transmutation", castingTime: "1 action", range: "Self", components: "V, S", duration: "1 minute", classes: ["Sorcerer", "Wizard"], description: "Roll a d20 at the end of each of your turns for the duration of the spell. On a roll of 11 or higher, you vanish from your current plane of existence and appear in the Ethereal Plane. At the start of your next turn, and when the spell ends if you are on the Ethereal Plane, you return to an unoccupied space of your choice that you can see within 10 feet of the space you vanished from.", source: "SRD" },
    { name: "Call Lightning", level: 3, school: "conjuration", castingTime: "1 action", range: "120 feet", components: "V, S", duration: "Concentration, up to 10 minutes", classes: ["Druid"], description: "A storm cloud appears in the shape of a cylinder that is 10 feet tall with a 60-foot radius, centered on a point you can see within range directly above you. When you cast the spell, choose a point you can see within range. A bolt of lightning flashes down from the cloud to that point. Each creature within 5 feet of that point must make a Dexterity saving throw. A creature takes 3d10 lightning damage on a failed save, or half as much damage on a successful one. On each of your turns until the spell ends, you can use your action to call down lightning in this way again.", source: "SRD" },
    { name: "Clairvoyance", level: 3, school: "divination", castingTime: "10 minutes", range: "1 mile", components: "V, S, M (a focus worth at least 100 gp, either a jeweled horn for hearing or a glass eye for seeing)", duration: "Concentration, up to 10 minutes", classes: ["Bard", "Cleric", "Sorcerer", "Wizard"], description: "You create an invisible sensor within range in a location familiar to you or in an obvious location that is unfamiliar to you. The sensor remains in place for the duration, and it can't be attacked or otherwise interacted with. When you cast the spell, you choose seeing or hearing. You can use the chosen sense through the sensor as if you were in its space.", source: "SRD" },
    { name: "Conjure Animals", level: 3, school: "conjuration", castingTime: "1 action", range: "60 feet", components: "V, S", duration: "Concentration, up to 1 hour", classes: ["Druid", "Ranger"], description: "You summon fey spirits that take the form of beasts and appear in unoccupied spaces that you can see within range. Choose one of the following options: one beast of CR 2 or lower, two beasts of CR 1 or lower, four beasts of CR 1/2 or lower, or eight beasts of CR 1/4 or lower. Each beast is also considered fey, and it disappears when it drops to 0 hit points or when the spell ends. The summoned creatures are friendly to you and your companions.", source: "SRD" },
    { name: "Conjure Barrage", level: 3, school: "conjuration", castingTime: "1 action", range: "Self (60-foot cone)", components: "V, S, M (one piece of ammunition or a thrown weapon)", duration: "Instantaneous", classes: ["Ranger"], description: "You throw a nonmagical weapon or fire a piece of nonmagical ammunition into the air to create a cone of identical weapons that shoot forward and then disappear. Each creature in a 60-foot cone must succeed on a Dexterity saving throw. A creature takes 3d8 damage on a failed save, or half as much damage on a successful one. The damage type is the same as that of the weapon or ammunition used as a component.", source: "SRD" },
    { name: "Create Food and Water", level: 3, school: "conjuration", castingTime: "1 action", range: "30 feet", components: "V, S", duration: "Instantaneous", classes: ["Cleric", "Paladin"], description: "You create 45 pounds of food and 30 gallons of water on the ground or in containers within range, enough to sustain up to fifteen humanoids or five steeds for 24 hours. The food is bland but nourishing, and spoils if uneaten after 24 hours. The water is clean and doesn't go bad.", source: "SRD" },
    { name: "Crusader's Mantle", level: 3, school: "evocation", castingTime: "1 action", range: "Self", components: "V", duration: "Concentration, up to 1 minute", classes: ["Paladin"], description: "Holy power radiates from you in an aura with a 30-foot radius, awakening boldness in friendly creatures. Until the spell ends, the aura moves with you, centered on you. While in the aura, each nonhostile creature in the aura (including you) deals an extra 1d4 radiant damage when it hits with a weapon attack.", source: "SRD" },
    { name: "Daylight", level: 3, school: "evocation", castingTime: "1 action", range: "60 feet", components: "V, S", duration: "1 hour", classes: ["Cleric", "Druid", "Paladin", "Ranger", "Sorcerer"], description: "A 60-foot-radius sphere of light spreads out from a point you choose within range. The sphere is bright light and sheds dim light for an additional 60 feet. If you chose a point on an object you are holding or one that isn't being worn or carried, the light shines from the object and moves with it. Completely covering the affected object with an opaque object, such as a bowl or a helm, blocks the light.", source: "SRD" },
    { name: "Elemental Weapon", level: 3, school: "transmutation", castingTime: "1 action", range: "Touch", components: "V, S", duration: "Concentration, up to 1 hour", classes: ["Paladin"], description: "A nonmagical weapon you touch becomes a magic weapon. Choose one of the following damage types: acid, cold, fire, lightning, or thunder. For the duration, the weapon has a +1 bonus to attack rolls and deals an extra 1d4 damage of the chosen type when it hits. At Higher Levels: When you cast this spell using a spell slot of 5th or 6th level, the bonus to attack rolls increases to +2 and the extra damage increases to 2d4. At 7th level or higher, the bonus increases to +3 and the extra damage increases to 3d4.", source: "SRD" },
    { name: "Fear", level: 3, school: "illusion", castingTime: "1 action", range: "Self (30-foot cone)", components: "V, S, M (a white feather or the heart of a hen)", duration: "Concentration, up to 1 minute", classes: ["Bard", "Sorcerer", "Warlock", "Wizard"], description: "You project a phantasmal image of a creature's worst fears. Each creature in a 30-foot cone must succeed on a Wisdom saving throw or drop whatever it is holding and become frightened for the duration. While frightened by this spell, a creature must take the Dash action and move away from you by the safest available route on each of its turns, unless there is nowhere to move.", source: "SRD" },
    { name: "Feign Death", level: 3, school: "necromancy", ritual: true, castingTime: "1 action", range: "Touch", components: "V, S, M (a pinch of graveyard dirt)", duration: "1 hour", classes: ["Bard", "Cleric", "Druid", "Wizard"], description: "You touch a willing creature and put it into a cataleptic state that is indistinguishable from death. For the spell's duration, or until you use an action to touch the target and dismiss the spell, the target appears dead to all outward inspection and to spells used to determine the target's status. The target is blinded and incapacitated, and its speed drops to 0. The target has resistance to all damage except psychic damage.", source: "SRD" },
    { name: "Gaseous Form", level: 3, school: "transmutation", castingTime: "1 action", range: "Touch", components: "V, S, M (a bit of gauze and a wisp of smoke)", duration: "Concentration, up to 1 hour", classes: ["Sorcerer", "Warlock", "Wizard"], description: "You transform a willing creature you touch, along with everything it's wearing and carrying, into a misty cloud for the duration. The spell ends if the creature drops to 0 hit points. An incorporeal creature isn't affected. While in this form, the target's only method of movement is a flying speed of 10 feet. The target has resistance to nonmagical damage, and it has advantage on Strength, Dexterity, and Constitution saving throws.", source: "SRD" },
    { name: "Glyph of Warding", level: 3, school: "abjuration", castingTime: "1 hour", range: "Touch", components: "V, S, M (incense and powdered diamond worth at least 200 gp, which the spell consumes)", duration: "Until dispelled or triggered", classes: ["Bard", "Cleric", "Wizard"], description: "When you cast this spell, you inscribe a glyph that later unleashes a magical effect. You inscribe it either on a surface or within an object that can be closed. The glyph can cover an area no larger than 10 feet in diameter. If the surface or object is moved more than 10 feet from where you cast this spell, the glyph is broken, and the spell ends without being triggered. You can choose from Explosive Runes (5d8 acid, cold, fire, lightning, or thunder damage) or Spell Glyph (store a spell of 3rd level or lower).", source: "SRD" },
    { name: "Hunger of Hadar", level: 3, school: "conjuration", castingTime: "1 action", range: "150 feet", components: "V, S, M (a pickled octopus tentacle)", duration: "Concentration, up to 1 minute", classes: ["Warlock"], description: "You open a gateway to the dark between the stars, a region infested with unknown horrors. A 20-foot-radius sphere of blackness and bitter cold appears, centered on a point within range and lasting for the duration. This void is filled with a cacophony of soft whispers and slurping noises that can be heard up to 30 feet away. No light can illuminate the area, and creatures fully within the area are blinded. Any creature that starts its turn in the area takes 2d6 cold damage. Any creature that ends its turn in the area must succeed on a Dexterity saving throw or take 2d6 acid damage.", source: "SRD" },
    { name: "Hypnotic Pattern", level: 3, school: "illusion", castingTime: "1 action", range: "120 feet", components: "S, M (a glowing stick of incense or a crystal vial filled with phosphorescent material)", duration: "Concentration, up to 1 minute", classes: ["Bard", "Sorcerer", "Warlock", "Wizard"], description: "You create a twisting pattern of colors that weaves through the air inside a 30-foot cube within range. The pattern appears for a moment and vanishes. Each creature in the area who sees the pattern must make a Wisdom saving throw. On a failed save, the creature becomes charmed for the duration. While charmed by this spell, the creature is incapacitated and has a speed of 0. The spell ends for an affected creature if it takes any damage or if someone else uses an action to shake the creature out of its stupor.", source: "SRD" },
    { name: "Leomund's Tiny Hut", level: 3, school: "evocation", ritual: true, castingTime: "1 minute", range: "Self (10-foot-radius hemisphere)", components: "V, S, M (a small crystal bead)", duration: "8 hours", classes: ["Bard", "Wizard"], description: "A 10-foot-radius immobile dome of force springs into existence around and above you and remains stationary for the duration. The spell ends if you leave its area. Nine creatures of Medium size or smaller can fit inside the dome with you. The spell fails if its area includes a larger creature or more than nine creatures. The atmosphere inside the space is comfortable and dry, regardless of the weather outside. Until the spell ends, you can command the interior to become dimly lit or dark.", source: "SRD" },
    { name: "Lightning Arrow", level: 3, school: "transmutation", castingTime: "1 bonus action", range: "Self", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Ranger"], description: "The next time you make a ranged weapon attack during the spell's duration, the weapon's ammunition, or the weapon itself if it's a thrown weapon, transforms into a bolt of lightning. Make the attack roll as normal. The target takes 4d8 lightning damage on a hit, or half as much damage on a miss, instead of the weapon's normal damage. Whether you hit or miss, each creature within 10 feet of the target must make a Dexterity saving throw. Each of these creatures takes 2d8 lightning damage on a failed save, or half as much damage on a successful one. At Higher Levels: The damage for both effects of the spell increases by 1d8 for each slot level above 3rd.", source: "SRD" },
    { name: "Locate Creature", level: 4, school: "divination", castingTime: "1 action", range: "Self", components: "V, S, M (a bit of fur from a bloodhound)", duration: "Concentration, up to 1 hour", classes: ["Bard", "Cleric", "Druid", "Paladin", "Ranger", "Wizard"], description: "Describe or name a creature that is familiar to you. You sense the direction to the creature's location, as long as that creature is within 1,000 feet of you. If the creature is moving, you know the direction of its movement. The spell can locate a specific creature known to you, or the nearest creature of a specific kind, as long as you have seen such a creature up close within 30 feet at least once. If the creature you described or named is in a different form, this spell doesn't locate the creature. Running water blocks the spell.", source: "SRD" },

    // 4th Level Spells
    { name: "Arcane Eye", level: 4, school: "divination", castingTime: "1 action", range: "30 feet", components: "V, S, M (a bit of bat fur)", duration: "Concentration, up to 1 hour", classes: ["Wizard"], description: "You create an invisible, magical eye within range that hovers in the air for the duration. You mentally receive visual information from the eye, which has normal vision and darkvision out to 30 feet. The eye can look in every direction. As an action, you can move the eye up to 30 feet in any direction. There is no limit to how far away from you the eye can move, but it can't enter another plane of existence. A solid barrier blocks the eye's movement, but the eye can pass through an opening as small as 1 inch in diameter.", source: "SRD" },
    { name: "Blight", level: 4, school: "necromancy", castingTime: "1 action", range: "30 feet", components: "V, S", duration: "Instantaneous", classes: ["Druid", "Sorcerer", "Warlock", "Wizard"], description: "Necromantic energy washes over a creature of your choice that you can see within range, draining moisture and vitality from it. The target must make a Constitution saving throw. The target takes 8d8 necrotic damage on a failed save, or half as much damage on a successful one. This spell has no effect on undead or constructs. If you target a plant creature or a magical plant, it makes the saving throw with disadvantage, and the spell deals maximum damage to it. At Higher Levels: The damage increases by 1d8 for each slot level above 4th.", source: "SRD" },
    { name: "Compulsion", level: 4, school: "enchantment", castingTime: "1 action", range: "30 feet", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Bard"], description: "Creatures of your choice that you can see within range and that can hear you must make a Wisdom saving throw. A target automatically succeeds on this saving throw if it can't be charmed. On a failed save, a target is affected by this spell. Until the spell ends, you can use a bonus action on each of your turns to designate a direction that is horizontal to you. Each affected target must use as much of its movement as possible to move in that direction on its next turn.", source: "SRD" },
    { name: "Confusion", level: 4, school: "enchantment", castingTime: "1 action", range: "90 feet", components: "V, S, M (three nut shells)", duration: "Concentration, up to 1 minute", classes: ["Bard", "Druid", "Sorcerer", "Wizard"], description: "This spell assaults and twists creatures' minds, spawning delusions and provoking uncontrolled action. Each creature in a 10-foot-radius sphere centered on a point you choose within range must succeed on a Wisdom saving throw when you cast this spell or be affected by it. An affected target can't take reactions and must roll a d10 at the start of each of its turns to determine its behavior for that turn. At Higher Levels: The radius of the sphere increases by 5 feet for each slot level above 4th.", source: "SRD" },
    { name: "Conjure Minor Elementals", level: 4, school: "conjuration", castingTime: "1 minute", range: "90 feet", components: "V, S", duration: "Concentration, up to 1 hour", classes: ["Druid", "Wizard"], description: "You summon elementals that appear in unoccupied spaces that you can see within range. You choose one of the following options: one elemental of CR 2 or lower, two elementals of CR 1 or lower, four elementals of CR 1/2 or lower, or eight elementals of CR 1/4 or lower. An elemental summoned by this spell disappears when it drops to 0 hit points or when the spell ends. The summoned creatures are friendly to you and your companions. They obey any verbal commands that you issue to them.", source: "SRD" },
    { name: "Conjure Woodland Beings", level: 4, school: "conjuration", castingTime: "1 action", range: "60 feet", components: "V, S, M (one holly berry per creature summoned)", duration: "Concentration, up to 1 hour", classes: ["Druid", "Ranger"], description: "You summon fey creatures that appear in unoccupied spaces that you can see within range. Choose one of the following options: one fey creature of CR 2 or lower, two fey creatures of CR 1 or lower, four fey creatures of CR 1/2 or lower, or eight fey creatures of CR 1/4 or lower. A summoned creature disappears when it drops to 0 hit points or when the spell ends. The summoned creatures are friendly to you and your companions.", source: "SRD" },
    { name: "Control Water", level: 4, school: "transmutation", castingTime: "1 action", range: "300 feet", components: "V, S, M (a drop of water and a pinch of dust)", duration: "Concentration, up to 10 minutes", classes: ["Cleric", "Druid", "Wizard"], description: "Until the spell ends, you control any freestanding water inside an area you choose that is a cube up to 100 feet on a side. You can choose from several effects when you cast this spell. Flood: You cause the water level of all standing water in the area to rise by as much as 20 feet. Part Water: You cause water in the area to move apart and create a trench. Redirect Flow: You cause flowing water in the area to move in a direction you choose. Whirlpool: You cause a whirlpool to form in a 50-foot square in the water.", source: "SRD" },
    { name: "Death Ward", level: 4, school: "abjuration", castingTime: "1 action", range: "Touch", components: "V, S", duration: "8 hours", classes: ["Cleric", "Paladin"], description: "You touch a creature and grant it a measure of protection from death. The first time the target would drop to 0 hit points as a result of taking damage, the target instead drops to 1 hit point, and the spell ends. If the spell is still in effect when the target is subjected to an effect that would kill it instantaneously without dealing damage, that effect is negated against the target, and the spell ends.", source: "SRD" },
    { name: "Divination", level: 4, school: "divination", ritual: true, castingTime: "1 action", range: "Self", components: "V, S, M (incense and a sacrificial offering appropriate to your religion, together worth at least 25 gp, which the spell consumes)", duration: "Instantaneous", classes: ["Cleric"], description: "Your magic and an offering put you in contact with a god or a god's servants. You ask a single question concerning a specific goal, event, or activity to occur within 7 days. The DM offers a truthful reply. The reply might be a short phrase, a cryptic rhyme, or an omen. The spell doesn't take into account any possible circumstances that might change the outcome.", source: "SRD" },
    { name: "Evard's Black Tentacles", level: 4, school: "conjuration", castingTime: "1 action", range: "90 feet", components: "V, S, M (a piece of tentacle from a giant octopus or a giant squid)", duration: "Concentration, up to 1 minute", classes: ["Wizard"], description: "Squirming, ebony tentacles fill a 20-foot square on ground that you can see within range. For the duration, these tentacles turn the ground in the area into difficult terrain. When a creature enters the affected area for the first time on a turn or starts its turn there, the creature must succeed on a Dexterity saving throw or take 3d6 bludgeoning damage and be restrained by the tentacles until the spell ends. A creature that starts its turn in the area and is already restrained by the tentacles takes 3d6 bludgeoning damage.", source: "SRD" },
    { name: "Fabricate", level: 4, school: "transmutation", castingTime: "10 minutes", range: "120 feet", components: "V, S", duration: "Instantaneous", classes: ["Wizard"], description: "You convert raw materials into products of the same material. For example, you can fabricate a wooden bridge from a clump of trees, a rope from a patch of hemp, and clothes from flax or wool. Choose raw materials that you can see within range. You can fabricate a Large or smaller object, or up to ten 5-foot cubes of objects, given a sufficient quantity of raw material. If you are working with metal, stone, or another mineral substance, the fabricated object can be no larger than Medium.", source: "SRD" },
    { name: "Fire Shield", level: 4, school: "evocation", castingTime: "1 action", range: "Self", components: "V, S, M (a bit of phosphorus or a firefly)", duration: "10 minutes", classes: ["Wizard"], description: "Thin and wispy flames wreathe your body for the duration, shedding bright light in a 10-foot radius and dim light for an additional 10 feet. You can end the spell early by using an action to dismiss it. The flames provide you with a warm shield or a chill shield, as you choose. The warm shield grants you resistance to cold damage, and the chill shield grants you resistance to fire damage. In addition, whenever a creature within 5 feet of you hits you with a melee attack, the shield erupts with flame. The attacker takes 2d8 fire damage from a warm shield, or 2d8 cold damage from a cold shield.", source: "SRD" },
    { name: "Freedom of Movement", level: 4, school: "abjuration", castingTime: "1 action", range: "Touch", components: "V, S, M (a leather strap, bound around the arm or a similar appendage)", duration: "1 hour", classes: ["Bard", "Cleric", "Druid", "Ranger"], description: "You touch a willing creature. For the duration, the target's movement is unaffected by difficult terrain, and spells and other magical effects can neither reduce the target's speed nor cause the target to be paralyzed or restrained. The target can also spend 5 feet of movement to automatically escape from nonmagical restraints, such as manacles or a creature that has it grappled. Finally, being underwater imposes no penalties on the target's movement or attacks.", source: "SRD" },
    { name: "Giant Insect", level: 4, school: "transmutation", castingTime: "1 action", range: "30 feet", components: "V, S", duration: "Concentration, up to 10 minutes", classes: ["Druid"], description: "You transform up to ten centipedes, three spiders, five wasps, or one scorpion within range into giant versions of their natural forms for the duration. A centipede becomes a giant centipede, a spider becomes a giant spider, a wasp becomes a giant wasp, and a scorpion becomes a giant scorpion. Each creature obeys your verbal commands, and in combat, they act on your turn each round.", source: "SRD" },
    { name: "Grasping Vine", level: 4, school: "conjuration", castingTime: "1 bonus action", range: "30 feet", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Druid", "Ranger"], description: "You conjure a vine that sprouts from the ground in an unoccupied space of your choice that you can see within range. When you cast this spell, you can direct the vine to lash out at a creature within 30 feet of it that you can see. That creature must succeed on a Dexterity saving throw or be pulled 20 feet directly toward the vine. Until the spell ends, you can direct the vine to lash out at the same creature or another one as a bonus action on each of your turns.", source: "SRD" },
    { name: "Greater Restoration", level: 5, school: "abjuration", castingTime: "1 action", range: "Touch", components: "V, S, M (diamond dust worth at least 100 gp, which the spell consumes)", duration: "Instantaneous", classes: ["Bard", "Cleric", "Druid"], description: "You imbue a creature you touch with positive energy to undo a debilitating effect. You can reduce the target's exhaustion level by one, or end one of the following effects on the target: one effect that charmed or petrified the target, one curse including attunement to a cursed magic item, any reduction to one of the target's ability scores, or one effect reducing the target's hit point maximum.", source: "SRD" },
    { name: "Guardian of Faith", level: 4, school: "conjuration", castingTime: "1 action", range: "30 feet", components: "V", duration: "8 hours", classes: ["Cleric"], description: "A Large spectral guardian appears and hovers for the duration in an unoccupied space of your choice that you can see within range. The guardian occupies that space and is indistinct except for a gleaming sword and shield emblazoned with the symbol of your deity. Any creature hostile to you that moves to a space within 10 feet of the guardian for the first time on a turn must succeed on a Dexterity saving throw. The creature takes 20 radiant damage on a failed save, or half as much damage on a successful one. The guardian vanishes when it has dealt a total of 60 damage.", source: "SRD" },
    { name: "Hallucinatory Terrain", level: 4, school: "illusion", castingTime: "10 minutes", range: "300 feet", components: "V, S, M (a stone, a twig, and a bit of green plant)", duration: "24 hours", classes: ["Bard", "Druid", "Warlock", "Wizard"], description: "You make natural terrain in a 150-foot cube in range look, sound, and smell like some other sort of natural terrain. Thus, open fields or a road can be made to resemble a swamp, hill, crevasse, or some other difficult or impassable terrain. A pond can be made to seem like a grassy meadow, a precipice like a gentle slope, or a rock-strewn gully like a wide and smooth road. Manufactured structures, equipment, and creatures within the area aren't changed in appearance.", source: "SRD" },
    { name: "Ice Storm", level: 4, school: "evocation", castingTime: "1 action", range: "300 feet", components: "V, S, M (a pinch of dust and a few drops of water)", duration: "Instantaneous", classes: ["Druid", "Sorcerer", "Wizard"], description: "A hail of rock-hard ice pounds to the ground in a 20-foot-radius, 40-foot-high cylinder centered on a point within range. Each creature in the cylinder must make a Dexterity saving throw. A creature takes 2d8 bludgeoning damage and 4d6 cold damage on a failed save, or half as much damage on a successful one. Hailstones turn the storm's area of effect into difficult terrain until the end of your next turn. At Higher Levels: The bludgeoning damage increases by 1d8 for each slot level above 4th.", source: "SRD" },

    // 5th Level Spells
    { name: "Animate Objects", level: 5, school: "transmutation", castingTime: "1 action", range: "120 feet", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Bard", "Sorcerer", "Wizard"], description: "Objects come to life at your command. Choose up to ten nonmagical objects within range that are not being worn or carried. Medium targets count as two objects, Large targets count as four objects, Huge targets count as eight objects. You can't animate any object larger than Huge. Each target animates and becomes a creature under your control until the spell ends or until reduced to 0 hit points.", source: "SRD" },
    { name: "Antilife Shell", level: 5, school: "abjuration", castingTime: "1 action", range: "Self (10-foot radius)", components: "V, S", duration: "Concentration, up to 1 hour", classes: ["Druid"], description: "A shimmering barrier extends out from you in a 10-foot radius and moves with you, remaining centered on you and hedging out creatures other than undead and constructs. The barrier lasts for the duration. The barrier prevents an affected creature from passing or reaching through. An affected creature can cast spells or make attacks with ranged or reach weapons through the barrier. If you move so that an affected creature is forced to pass through the barrier, the spell ends.", source: "SRD" },
    { name: "Awaken", level: 5, school: "transmutation", castingTime: "8 hours", range: "Touch", components: "V, S, M (an agate worth at least 1,000 gp, which the spell consumes)", duration: "Instantaneous", classes: ["Bard", "Druid"], description: "After spending the casting time tracing magical pathways within a precious gemstone, you touch a Huge or smaller beast or plant. The target must have either no Intelligence score or an Intelligence of 3 or less. The target gains an Intelligence of 10. The target also gains the ability to speak one language you know. If the target is a plant, it gains the ability to move its limbs, roots, vines, creepers, and so forth, and it gains senses similar to a human's. The awakened beast or plant is charmed by you for 30 days or until you or your companions do anything harmful to it.", source: "SRD" },
    { name: "Bigby's Hand", level: 5, school: "evocation", castingTime: "1 action", range: "120 feet", components: "V, S, M (an eggshell and a snakeskin glove)", duration: "Concentration, up to 1 minute", classes: ["Wizard"], description: "You create a Large hand of shimmering, translucent force in an unoccupied space that you can see within range. The hand lasts for the spell's duration, and it moves at your command, mimicking the movements of your own hand. The hand is an object that has AC 20 and hit points equal to your hit point maximum. The hand can punch, push, grasp, or interpose itself between you and attacks.", source: "SRD" },
    { name: "Circle of Power", level: 5, school: "abjuration", castingTime: "1 action", range: "Self (30-foot radius)", components: "V", duration: "Concentration, up to 10 minutes", classes: ["Paladin"], description: "Divine energy radiates from you, distorting and diffusing magical energy within 30 feet of you. Until the spell ends, the sphere moves with you, centered on you. For the duration, each friendly creature in the area (including you) has advantage on saving throws against spells and other magical effects. Additionally, when an affected creature succeeds on a saving throw made against a spell or magical effect that allows it to make a saving throw to take only half damage, it instead takes no damage if it succeeds on the saving throw.", source: "SRD" },
    { name: "Cloudkill", level: 5, school: "conjuration", castingTime: "1 action", range: "120 feet", components: "V, S", duration: "Concentration, up to 10 minutes", classes: ["Sorcerer", "Wizard"], description: "You create a 20-foot-radius sphere of poisonous, yellow-green fog centered on a point you choose within range. The fog spreads around corners. It lasts for the duration or until strong wind disperses the fog. Its area is heavily obscured. When a creature enters the spell's area for the first time on a turn or starts its turn there, that creature must make a Constitution saving throw. The creature takes 5d8 poison damage on a failed save, or half as much damage on a successful one. At Higher Levels: The damage increases by 1d8 for each slot level above 5th.", source: "SRD" },
    { name: "Commune", level: 5, school: "divination", ritual: true, castingTime: "1 minute", range: "Self", components: "V, S, M (incense and a vial of holy or unholy water)", duration: "1 minute", classes: ["Cleric"], description: "You contact your deity or a divine proxy and ask up to three questions that can be answered with a yes or no. You must ask your questions before the spell ends. You receive a correct answer for each question. Divine beings aren't necessarily omniscient, so you might receive 'unclear' as an answer if a question pertains to information that lies beyond the deity's knowledge.", source: "SRD" },
    { name: "Commune with Nature", level: 5, school: "divination", ritual: true, castingTime: "1 minute", range: "Self", components: "V, S", duration: "Instantaneous", classes: ["Druid", "Ranger"], description: "You briefly become one with nature and gain knowledge of the surrounding territory. In the outdoors, the spell gives you knowledge of the land within 3 miles of you. In caves and other natural underground settings, the radius is limited to 300 feet. The spell doesn't function where nature has been replaced by construction, such as in dungeons and towns. You instantly gain knowledge of up to three facts of your choice about any of the following subjects: terrain and bodies of water, prevalent plants, minerals, animals, or peoples, powerful celestials, fey, fiends, elementals, or undead, influence from other planes of existence, or buildings.", source: "SRD" },
    { name: "Conjure Elemental", level: 5, school: "conjuration", castingTime: "1 minute", range: "90 feet", components: "V, S, M (burning incense for air, soft clay for earth, sulfur and phosphorus for fire, or water and sand for water)", duration: "Concentration, up to 1 hour", classes: ["Druid", "Wizard"], description: "You call forth an elemental servant. Choose an area of air, earth, fire, or water that fills a 10-foot cube within range. An elemental of challenge rating 5 or lower appropriate to the area you chose appears in an unoccupied space within 10 feet of it. The elemental disappears when it drops to 0 hit points or when the spell ends. The elemental is friendly to you and your companions for the duration. If your concentration is broken, the elemental doesn't disappear but becomes hostile toward you and your companions.", source: "SRD" },
    { name: "Conjure Volley", level: 5, school: "conjuration", castingTime: "1 action", range: "150 feet", components: "V, S, M (one piece of ammunition or one thrown weapon)", duration: "Instantaneous", classes: ["Ranger"], description: "You fire a piece of nonmagical ammunition from a ranged weapon or throw a nonmagical weapon into the air and choose a point within range. Hundreds of duplicates of the ammunition or weapon fall in a volley from above and then disappear. Each creature in a 40-foot-radius, 20-foot-high cylinder centered on that point must make a Dexterity saving throw. A creature takes 8d8 damage on a failed save, or half as much damage on a successful one. The damage type is the same as that of the ammunition or weapon.", source: "SRD" },
    { name: "Contagion", level: 5, school: "necromancy", castingTime: "1 action", range: "Touch", components: "V, S", duration: "7 days", classes: ["Cleric", "Druid"], description: "Your touch inflicts disease. Make a melee spell attack against a creature within your reach. On a hit, the creature is poisoned. At the end of each of the poisoned target's turns, the target must make a Constitution saving throw. If the target succeeds on three of these saves, it is no longer poisoned, and the spell ends. If the target fails three of these saves, the target is no longer poisoned, but choose one of the diseases: blinding sickness, filth fever, flesh rot, mindfire, seizure, or slimy doom.", source: "SRD" },
    { name: "Contact Other Plane", level: 5, school: "divination", ritual: true, castingTime: "1 minute", range: "Self", components: "V", duration: "1 minute", classes: ["Warlock", "Wizard"], description: "You mentally contact a demigod, the spirit of a long-dead sage, or some other mysterious entity from another plane. Contacting this extraplanar intelligence can strain or even break your mind. When you cast this spell, make a DC 15 Intelligence saving throw. On a failure, you take 6d6 psychic damage and are insane until you finish a long rest. On a successful save, you can ask the entity up to five questions. The entity knows only what it has learned during its existence and might answer in a cryptic manner.", source: "SRD" },
    { name: "Destructive Wave", level: 5, school: "evocation", castingTime: "1 action", range: "Self (30-foot radius)", components: "V", duration: "Instantaneous", classes: ["Paladin"], description: "You strike the ground, creating a burst of divine energy that ripples outward from you. Each creature you choose within 30 feet of you must succeed on a Constitution saving throw or take 5d6 thunder damage, as well as 5d6 radiant or necrotic damage (your choice), and be knocked prone. A creature that succeeds on its saving throw takes half as much damage and isn't knocked prone.", source: "SRD" },
    { name: "Dominate Person", level: 5, school: "enchantment", castingTime: "1 action", range: "60 feet", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Bard", "Sorcerer", "Wizard"], description: "You attempt to beguile a humanoid that you can see within range. It must succeed on a Wisdom saving throw or be charmed by you for the duration. If you or creatures that are friendly to you are fighting it, it has advantage on the saving throw. While the target is charmed, you have a telepathic link with it as long as the two of you are on the same plane of existence. You can use this telepathic link to issue commands to the creature while you are conscious.", source: "SRD" },
    { name: "Dream", level: 5, school: "illusion", castingTime: "1 minute", range: "Special", components: "V, S, M (a handful of sand, a dab of ink, and a writing quill plucked from a sleeping bird)", duration: "8 hours", classes: ["Bard", "Warlock", "Wizard"], description: "This spell shapes a creature's dreams. Choose a creature known to you as the target of this spell. The target must be on the same plane of existence as you. Creatures that don't sleep, such as elves, can't be contacted by this spell. You, or a willing creature you touch, enters a trance state, acting as a messenger. While in the trance, the messenger is aware of their surroundings, but can't take actions or move.", source: "SRD" },
    { name: "Flame Strike", level: 5, school: "evocation", castingTime: "1 action", range: "60 feet", components: "V, S, M (pinch of sulfur)", duration: "Instantaneous", classes: ["Cleric"], description: "A vertical column of divine fire roars down from the heavens in a location you specify. Each creature in a 10-foot-radius, 40-foot-high cylinder centered on a point within range must make a Dexterity saving throw. A creature takes 4d6 fire damage and 4d6 radiant damage on a failed save, or half as much damage on a successful one. At Higher Levels: The fire damage or the radiant damage (your choice) increases by 1d6 for each slot level above 5th.", source: "SRD" },
    { name: "Geas", level: 5, school: "enchantment", castingTime: "1 minute", range: "60 feet", components: "V", duration: "30 days", classes: ["Bard", "Cleric", "Druid", "Paladin", "Wizard"], description: "You place a magical command on a creature that you can see within range, forcing it to carry out some service or refrain from some action or course of activity as you decide. If the creature can understand you, it must succeed on a Wisdom saving throw or become charmed by you for the duration. While the creature is charmed by you, it takes 5d10 psychic damage each time it acts in a manner directly counter to your instructions, but no more than once each day. At Higher Levels: Duration increases with higher level slots.", source: "SRD" },
    { name: "Hallow", level: 5, school: "evocation", castingTime: "24 hours", range: "Touch", components: "V, S, M (herbs, oils, and incense worth at least 1,000 gp, which the spell consumes)", duration: "Until dispelled", classes: ["Cleric"], description: "You touch a point and infuse an area around it with holy or unholy power. The area can have a radius up to 60 feet, and the spell fails if the radius includes an area already under the effect a hallow spell. The affected area is subject to the following effects: celestials, elementals, fey, fiends, and undead can't enter the area, nor can such creatures charm, frighten, or possess creatures within it. You can also bind an extra effect to the area from a list including Courage, Darkness, Daylight, Energy Protection, Everlasting Rest, Extradimensional Interference, Fear, Silence, Tongues, and Energy Vulnerability.", source: "SRD" },
    { name: "Insect Plague", level: 5, school: "conjuration", castingTime: "1 action", range: "300 feet", components: "V, S, M (a few grains of sugar, some kernels of grain, and a smear of fat)", duration: "Concentration, up to 10 minutes", classes: ["Cleric", "Druid", "Sorcerer"], description: "Swarming, biting locusts fill a 20-foot-radius sphere centered on a point you choose within range. The sphere spreads around corners. The sphere remains for the duration, and its area is lightly obscured. The sphere's area is difficult terrain. When the area appears, each creature in it must make a Constitution saving throw. A creature takes 4d10 piercing damage on a failed save, or half as much damage on a successful one. A creature must also make this saving throw when it enters the spell's area for the first time on a turn or ends its turn there. At Higher Levels: The damage increases by 1d10 for each slot level above 5th.", source: "SRD" },
    { name: "Legend Lore", level: 5, school: "divination", castingTime: "10 minutes", range: "Self", components: "V, S, M (incense worth at least 250 gp, which the spell consumes, and four ivory strips worth at least 50 gp each)", duration: "Instantaneous", classes: ["Bard", "Cleric", "Wizard"], description: "Name or describe a person, place, or object. The spell brings to your mind a brief summary of the significant lore about the thing you named. The lore might consist of current tales, forgotten stories, or even secret lore that has never been widely known. If the thing you named isn't of legendary importance, you gain no information. The more information you already have about the thing, the more precise and detailed the information you receive is.", source: "SRD" },

    // 6th Level Spells
    { name: "Blade Barrier", level: 6, school: "evocation", castingTime: "1 action", range: "90 feet", components: "V, S", duration: "Concentration, up to 10 minutes", classes: ["Cleric"], description: "You create a vertical wall of whirling, razor-sharp blades made of magical energy. The wall appears within range and lasts for the duration. You can make a straight wall up to 100 feet long, 20 feet high, and 5 feet thick, or a ringed wall up to 60 feet in diameter, 20 feet high, and 5 feet thick. The wall provides three-quarters cover to creatures behind it, and its space is difficult terrain. When a creature enters the wall's area for the first time on a turn or starts its turn there, the creature must make a Dexterity saving throw. On a failed save, the creature takes 6d10 slashing damage. On a successful save, the creature takes half as much damage.", source: "SRD" },
    { name: "Circle of Death", level: 6, school: "necromancy", castingTime: "1 action", range: "150 feet", components: "V, S, M (the powder of a crushed black pearl worth at least 500 gp)", duration: "Instantaneous", classes: ["Sorcerer", "Warlock", "Wizard"], description: "A sphere of negative energy ripples out in a 60-foot-radius sphere from a point within range. Each creature in that area must make a Constitution saving throw. A target takes 8d6 necrotic damage on a failed save, or half as much damage on a successful one. At Higher Levels: The damage increases by 2d6 for each slot level above 6th.", source: "SRD" },
    { name: "Conjure Fey", level: 6, school: "conjuration", castingTime: "1 minute", range: "90 feet", components: "V, S", duration: "Concentration, up to 1 hour", classes: ["Druid", "Warlock"], description: "You summon a fey creature of challenge rating 6 or lower, or a fey spirit that takes the form of a beast of challenge rating 6 or lower. It appears in an unoccupied space that you can see within range. The fey creature disappears when it drops to 0 hit points or when the spell ends. The fey creature is friendly to you and your companions for the duration. If your concentration is broken, the fey creature doesn't disappear but becomes hostile toward you and your companions.", source: "SRD" },
    { name: "Contingency", level: 6, school: "evocation", castingTime: "10 minutes", range: "Self", components: "V, S, M (a statuette of yourself carved from ivory and decorated with gems worth at least 1,500 gp)", duration: "10 days", classes: ["Wizard"], description: "Choose a spell of 5th level or lower that you can cast, that has a casting time of 1 action, and that can target you. You cast that spellcalled the contingent spellas part of casting contingency, expending spell slots for both, but the contingent spell doesn't come into effect. Instead, it takes effect when a certain circumstance occurs. You describe that circumstance when you cast the two spells. The contingent spell takes effect immediately after the circumstance is met for the first time.", source: "SRD" },
    { name: "Create Undead", level: 6, school: "necromancy", castingTime: "1 minute", range: "10 feet", components: "V, S, M (one clay pot filled with grave dirt, one clay pot filled with brackish water, and one 150 gp black onyx stone for each corpse)", duration: "Instantaneous", classes: ["Cleric", "Warlock", "Wizard"], description: "You can cast this spell only at night. Choose up to three corpses of Medium or Small humanoids within range. Each corpse becomes a ghoul under your control. As a bonus action on each of your turns, you can mentally command any creature you animated with this spell if the creature is within 120 feet of you. At Higher Levels: When you cast this spell using a 7th-level slot, you can animate or reassert control over four ghouls. When you cast this spell using an 8th-level slot, you can animate or reassert control over five ghouls or two ghasts or wights. When you cast this spell using a 9th-level slot, you can animate or reassert control over six ghouls, three ghasts or wights, or two mummies.", source: "SRD" },
    { name: "Drawmij's Instant Summons", level: 6, school: "conjuration", ritual: true, castingTime: "1 minute", range: "Touch", components: "V, S, M (a sapphire worth 1,000 gp)", duration: "Until dispelled", classes: ["Wizard"], description: "You touch an object weighing 10 pounds or less whose longest dimension is 6 feet or less. The spell leaves an invisible mark on its surface and invisibly inscribes the name of the item on the sapphire you use as the material component. Each time you cast this spell, you must use a different sapphire. At any time thereafter, you can use your action to speak the item's name and crush the sapphire. The item instantly appears in your hand regardless of physical or planar distances.", source: "SRD" },
    { name: "Eyebite", level: 6, school: "necromancy", castingTime: "1 action", range: "Self", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Bard", "Sorcerer", "Warlock", "Wizard"], description: "For the spell's duration, your eyes become an inky void imbued with dread power. One creature of your choice within 60 feet of you that you can see must succeed on a Wisdom saving throw or be affected by one of the following effects of your choice for the duration: Asleep (falls unconscious), Panicked (frightened of you and must Dash away), or Sickened (disadvantage on attacks and ability checks). Each turn until the spell ends, you can use your action to target another creature but can't target a creature that has already succeeded on a saving throw against this casting of eyebite.", source: "SRD" },
    { name: "Find the Path", level: 6, school: "divination", castingTime: "1 minute", range: "Self", components: "V, S, M (a set of divinatory toolssuch as bones, ivory sticks, cards, teeth, or carved runesworth 100 gp and an object from the location you wish to find)", duration: "Concentration, up to 1 day", classes: ["Bard", "Cleric", "Druid"], description: "This spell allows you to find the shortest, most direct physical route to a specific fixed location that you are familiar with on the same plane of existence. If you name a destination on another plane of existence, a destination that moves, or a destination that isn't specific, the spell fails. For the duration, as long as you are on the same plane of existence as the destination, you know how far it is and in what direction it lies. While you are traveling there, whenever you are presented with a choice of paths along the way, you automatically determine which path is the shortest and most direct route.", source: "SRD" },
    { name: "Flesh to Stone", level: 6, school: "transmutation", castingTime: "1 action", range: "60 feet", components: "V, S, M (a pinch of lime, water, and earth)", duration: "Concentration, up to 1 minute", classes: ["Warlock", "Wizard"], description: "You attempt to turn one creature that you can see within range into stone. If the target's body is made of flesh, the creature must make a Constitution saving throw. On a failed save, it is restrained as its flesh begins to harden. On a successful save, the creature isn't affected. A creature restrained by this spell must make another Constitution saving throw at the end of each of its turns. If it successfully saves against this spell three times, the spell ends. If it fails its saves three times, it is turned to stone and subjected to the petrified condition for the duration. The successes and failures don't need to be consecutive.", source: "SRD" },
    { name: "Forbiddance", level: 6, school: "abjuration", ritual: true, castingTime: "10 minutes", range: "Touch", components: "V, S, M (a sprinkling of holy water, rare incense, and powdered ruby worth at least 1,000 gp)", duration: "1 day", classes: ["Cleric"], description: "You create a ward against magical travel that protects up to 40,000 square feet of floor space to a height of 30 feet above the floor. For the duration, creatures can't teleport into the area or use portals to enter the area. The spell proofs the area against planar travel. Additionally, the spell damages types of creatures that you choose when you cast it. Choose one or more of the following: celestials, elementals, fey, fiends, and undead. When a chosen creature enters the spell's area for the first time on a turn or starts its turn there, the creature takes 5d10 radiant or necrotic damage (your choice when you cast this spell).", source: "SRD" },
    { name: "Globe of Invulnerability", level: 6, school: "abjuration", castingTime: "1 action", range: "Self (10-foot radius)", components: "V, S, M (a glass or crystal bead that shatters when the spell ends)", duration: "Concentration, up to 1 minute", classes: ["Sorcerer", "Wizard"], description: "An immobile, faintly shimmering barrier springs into existence in a 10-foot radius around you and remains for the duration. Any spell of 5th level or lower cast from outside the barrier can't affect creatures or objects within it, even if the spell is cast using a higher level spell slot. Such a spell can target creatures and objects within the barrier, but the spell has no effect on them. Similarly, the area within the barrier is excluded from the areas affected by such spells. At Higher Levels: The barrier blocks spells of one level higher for each slot level above 6th.", source: "SRD" },
    { name: "Guards and Wards", level: 6, school: "abjuration", castingTime: "10 minutes", range: "Touch", components: "V, S, M (burning incense, a small measure of brimstone and oil, a knotted string, a small amount of umber hulk blood, and a small silver rod worth at least 10 gp)", duration: "24 hours", classes: ["Bard", "Wizard"], description: "You create a ward that protects up to 2,500 square feet of floor space. The warded area can be up to 20 feet tall, and shaped as you desire. You can ward several stories of a stronghold by dividing the area among them, as long as you can walk into each contiguous area while you are casting the spell. You can place the following magical effects: corridors filled with fog, doors magically locked, stairs filled with webs, and other effects. You can also place your choice of arcane lock, dancing lights, magic mouth, stinking cloud, gust of wind, and suggestion in the warded area.", source: "SRD" },

    // 7th Level Spells
    { name: "Conjure Celestial", level: 7, school: "conjuration", castingTime: "1 minute", range: "90 feet", components: "V, S", duration: "Concentration, up to 1 hour", classes: ["Cleric"], description: "You summon a celestial of challenge rating 4 or lower, which appears in an unoccupied space that you can see within range. The celestial disappears when it drops to 0 hit points or when the spell ends. The celestial is friendly to you and your companions for the duration. Roll initiative for the celestial, which has its own turns. It obeys any verbal commands that you issue to it, as long as they don't violate its alignment. If you don't issue any commands to the celestial, it defends itself from hostile creatures but otherwise takes no actions. At Higher Levels: When you cast this spell using a 9th-level spell slot, you summon a celestial of challenge rating 5 or lower.", source: "SRD" },
    { name: "Delayed Blast Fireball", level: 7, school: "evocation", castingTime: "1 action", range: "150 feet", components: "V, S, M (a tiny ball of bat guano and sulfur)", duration: "Concentration, up to 1 minute", classes: ["Sorcerer", "Wizard"], description: "A beam of yellow light flashes from your pointing finger, then condenses to linger at a chosen point within range as a glowing bead for the duration. When the spell ends, either because your concentration is broken or because you decide to end it, the bead blossoms with a low roar into an explosion of flame that spreads around corners. Each creature in a 20-foot-radius sphere centered on that point must make a Dexterity saving throw. A creature takes fire damage equal to the total accumulated damage on a failed save, or half as much damage on a successful one. The spell's base damage is 12d6. If at the end of your turn the bead has not yet detonated, the damage increases by 1d6. At Higher Levels: The base damage increases by 1d6 for each slot level above 7th.", source: "SRD" },
    { name: "Divine Word", level: 7, school: "evocation", castingTime: "1 bonus action", range: "30 feet", components: "V", duration: "Instantaneous", classes: ["Cleric"], description: "You utter a divine word, imbued with the power that shaped the world at the dawn of creation. Choose any number of creatures you can see within range. Each creature that can hear you must make a Charisma saving throw. On a failed save, a creature suffers an effect based on its current hit points: 50 hp or fewer (deafened for 1 minute), 40 hp or fewer (deafened and blinded for 10 minutes), 30 hp or fewer (blinded, deafened, and stunned for 1 hour), 20 hp or fewer (killed instantly). Any celestial, elemental, fey, or fiend that fails its save is forced back to its plane of origin.", source: "SRD" },
    { name: "Etherealness", level: 7, school: "transmutation", castingTime: "1 action", range: "Self", components: "V, S", duration: "Up to 8 hours", classes: ["Bard", "Cleric", "Sorcerer", "Warlock", "Wizard"], description: "You step into the border regions of the Ethereal Plane, in the area where it overlaps with your current plane. You remain in the Border Ethereal for the duration or until you use your action to dismiss the spell. During this time, you can move in any direction. If you move up or down, every foot of movement costs an extra foot. You can see and hear the plane you originated from, but everything there looks gray, and you can't see anything more than 60 feet away. While on the Ethereal Plane, you can only affect and be affected by other creatures on that plane.", source: "SRD" },
    { name: "Fire Storm", level: 7, school: "evocation", castingTime: "1 action", range: "150 feet", components: "V, S", duration: "Instantaneous", classes: ["Cleric", "Druid", "Sorcerer"], description: "A storm made up of sheets of roaring flame appears in a location you choose within range. The area of the storm consists of up to ten 10-foot cubes, which you can arrange as you wish. Each cube must have at least one face adjacent to the face of another cube. Each creature in the area must make a Dexterity saving throw. It takes 7d10 fire damage on a failed save, or half as much damage on a successful one. The fire damages objects in the area and ignites flammable objects that aren't being worn or carried. If you choose, plant life in the area is unaffected by this spell.", source: "SRD" },
    { name: "Forcecage", level: 7, school: "evocation", castingTime: "1 action", range: "100 feet", components: "V, S, M (ruby dust worth 1,500 gp)", duration: "1 hour", classes: ["Bard", "Warlock", "Wizard"], description: "An immobile, invisible, cube-shaped prison composed of magical force springs into existence around an area you choose within range. The prison can be a cage or a solid box, as you choose. A prison in the shape of a cage can be up to 20 feet on a side and is made from 1/2-inch diameter bars spaced 1/2 inch apart. A prison in the shape of a box can be up to 10 feet on a side, creating a solid barrier that prevents any matter from passing through it. When you cast the spell, any creature that is completely inside the cage's area is trapped. Creatures only partially within the area, or those too large to fit inside the area, are pushed away from the center of the area until they are completely outside the area.", source: "SRD" },
    { name: "Leomund's Secret Chest", level: 4, school: "conjuration", castingTime: "1 action", range: "Touch", components: "V, S, M (an exquisite chest, 3 feet by 2 feet by 2 feet, constructed from rare materials worth at least 5,000 gp, and a Tiny replica made from the same materials worth at least 50 gp)", duration: "Instantaneous", classes: ["Wizard"], description: "You hide a chest, and all its contents, on the Ethereal Plane. You must touch the chest and the miniature replica that serves as a material component for the spell. The chest can contain up to 12 cubic feet of nonliving material. While the chest remains on the Ethereal Plane, you can use an action and touch the replica to recall the chest. It appears in an unoccupied space on the ground within 5 feet of you. You can send the chest back to the Ethereal Plane by using an action and touching both the chest and the replica.", source: "SRD" },
    { name: "Glibness", level: 8, school: "transmutation", castingTime: "1 action", range: "Self", components: "V", duration: "1 hour", classes: ["Bard", "Warlock"], description: "Until the spell ends, when you make a Charisma check, you can replace the number you roll with a 15. Additionally, no matter what you say, magic that would determine if you are telling the truth indicates that you are being truthful.", source: "SRD" },
    { name: "Holy Aura", level: 8, school: "abjuration", castingTime: "1 action", range: "Self", components: "V, S, M (a tiny reliquary worth at least 1,000 gp containing a sacred relic, such as a scrap of cloth from a saint's robe or a piece of parchment from a religious text)", duration: "Concentration, up to 1 minute", classes: ["Cleric"], description: "Divine light washes out from you and coalesces in a soft radiance in a 30-foot radius around you. Creatures of your choice in that radius when you cast this spell shed dim light in a 5-foot radius and have advantage on all saving throws, and other creatures have disadvantage on attack rolls against them until the spell ends. In addition, when a fiend or an undead hits an affected creature with a melee attack, the aura flashes with brilliant light. The attacker must succeed on a Constitution saving throw or be blinded until the spell ends.", source: "SRD" },

    // 8th Level Spells
    { name: "Antimagic Field", level: 8, school: "abjuration", castingTime: "1 action", range: "Self (10-foot-radius sphere)", components: "V, S, M (a pinch of powdered iron or iron filings)", duration: "Concentration, up to 1 hour", classes: ["Cleric", "Wizard"], description: "A 10-foot-radius invisible sphere of antimagic surrounds you. This area is divorced from the magical energy that suffuses the multiverse. Within the sphere, spells can't be cast, summoned creatures disappear, and even magic items become mundane. Until the spell ends, the sphere moves with you, centered on you. Spells and other magical effects, except those created by an artifact or a deity, are suppressed in the sphere and can't protrude into it. A slot expended to cast a suppressed spell is consumed.", source: "SRD" },
    { name: "Antipathy/Sympathy", level: 8, school: "enchantment", castingTime: "1 hour", range: "60 feet", components: "V, S, M (either a lump of alum soaked in vinegar for the antipathy effect or a drop of honey for the sympathy effect)", duration: "10 days", classes: ["Druid", "Wizard"], description: "This spell attracts or repels creatures of your choice. You target something within range, either a Huge or smaller object or creature or an area that is no larger than a 200-foot cube. Then specify a kind of intelligent creature, such as red dragons, goblins, or vampires. You invest the target with an aura that either attracts or repels the specified creatures for the duration. Choose antipathy or sympathy as the aura's effect. Antipathy: The enchantment causes creatures to feel an intense urge to leave the area and avoid the target. Sympathy: The enchantment causes creatures to feel an intense urge to approach the target.", source: "SRD" },
    { name: "Clone", level: 8, school: "necromancy", castingTime: "1 hour", range: "Touch", components: "V, S, M (a diamond worth at least 1,000 gp and at least 1 cubic inch of flesh of the creature that is to be cloned, which the spell consumes, and a vessel worth at least 2,000 gp that has a sealable lid and is large enough to hold a Medium creature, such as a huge urn, coffin, mud-filled cyst in the ground, or crystal container filled with salt water)", duration: "Instantaneous", classes: ["Wizard"], description: "This spell grows an inert duplicate of a living creature as a safeguard against death. This clone forms inside a sealed vessel and grows to full size and maturity after 120 days; you can also choose to have the clone be a younger version of the same creature. It remains inert and endures indefinitely, as long as its vessel remains undisturbed. At any time after the clone matures, if the original creature dies, its soul transfers to the clone, provided that the soul is free and willing to return.", source: "SRD" },
    { name: "Control Weather", level: 8, school: "transmutation", castingTime: "10 minutes", range: "Self (5-mile radius)", components: "V, S, M (burning incense and bits of earth and wood mixed in water)", duration: "Concentration, up to 8 hours", classes: ["Cleric", "Druid", "Wizard"], description: "You take control of the weather within 5 miles of you for the duration. You must be outdoors to cast this spell. Moving to a place where you don't have a clear path to the sky ends the spell early. When you cast the spell, you change the current weather conditions, which are determined by the DM based on the climate and season. You can change precipitation, temperature, and wind. It takes 1d4 x 10 minutes for the new conditions to take effect. Once they do so, you can change the conditions again.", source: "SRD" },
    { name: "Demiplane", level: 8, school: "conjuration", castingTime: "1 action", range: "60 feet", components: "S", duration: "1 hour", classes: ["Warlock", "Wizard"], description: "You create a shadowy door on a flat solid surface that you can see within range. The door is large enough to allow Medium creatures to pass through unhindered. When opened, the door leads to a demiplane that appears to be an empty room 30 feet in each dimension, made of wood or stone. When the spell ends, the door disappears, and any creatures or objects inside the demiplane remain trapped there, as the door also disappears from the other side. Each time you cast this spell, you can create a new demiplane, or have the shadowy door connect to a demiplane you created with a previous casting of this spell.", source: "SRD" },
    { name: "Dominate Beast", level: 4, school: "enchantment", castingTime: "1 action", range: "60 feet", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Druid", "Sorcerer"], description: "You attempt to beguile a beast that you can see within range. It must succeed on a Wisdom saving throw or be charmed by you for the duration. If you or creatures that are friendly to you are fighting it, it has advantage on the saving throw. While the beast is charmed, you have a telepathic link with it as long as the two of you are on the same plane of existence. You can use this telepathic link to issue commands to the creature while you are conscious (no action required), which it does its best to obey. At Higher Levels: Duration increases with higher level slots.", source: "SRD" },
    { name: "Earthquake", level: 8, school: "evocation", castingTime: "1 action", range: "500 feet", components: "V, S, M (a pinch of dirt, a piece of rock, and a lump of clay)", duration: "Concentration, up to 1 minute", classes: ["Cleric", "Druid", "Sorcerer"], description: "You create a seismic disturbance at a point on the ground that you can see within range. For the duration, an intense tremor rips through the ground in a 100-foot-radius circle centered on that point and shakes creatures and structures in contact with the ground in that area. The ground in the area becomes difficult terrain. Each creature on the ground that is concentrating must make a Constitution saving throw. On a failed save, the creature's concentration is broken. Each creature on the ground must make a Dexterity saving throw at the end of each turn or be knocked prone.", source: "SRD" },
    { name: "Feeblemind", level: 8, school: "enchantment", castingTime: "1 action", range: "150 feet", components: "V, S, M (a handful of clay, crystal, glass, or mineral spheres)", duration: "Instantaneous", classes: ["Bard", "Druid", "Warlock", "Wizard"], description: "You blast the mind of a creature that you can see within range, attempting to shatter its intellect and personality. The target takes 4d6 psychic damage and must make an Intelligence saving throw. On a failed save, the creature's Intelligence and Charisma scores become 1. The creature can't cast spells, activate magic items, understand language, or communicate in any intelligible way. The creature can, however, identify its friends, follow them, and even protect them. At the end of every 30 days, the creature can repeat its saving throw against this spell.", source: "SRD" },
    { name: "Incendiary Cloud", level: 8, school: "conjuration", castingTime: "1 action", range: "150 feet", components: "V, S", duration: "Concentration, up to 1 minute", classes: ["Sorcerer", "Wizard"], description: "A swirling cloud of smoke shot through with white-hot embers appears in a 20-foot-radius sphere centered on a point within range. The cloud spreads around corners and is heavily obscured. It lasts for the duration or until a wind of moderate or greater speed (at least 10 miles per hour) disperses it. When the cloud appears, each creature in it must make a Dexterity saving throw. A creature takes 10d8 fire damage on a failed save, or half as much damage on a successful one. A creature must also make this saving throw when it enters the spell's area for the first time on a turn or ends its turn there.", source: "SRD" },

    // 9th Level Spells
    { name: "Astral Projection", level: 9, school: "necromancy", castingTime: "1 hour", range: "10 feet", components: "V, S, M (for each creature you affect with this spell, you must provide one jacinth worth at least 1,000 gp and one ornately carved bar of silver worth at least 100 gp, all of which the spell consumes)", duration: "Special", classes: ["Cleric", "Warlock", "Wizard"], description: "You and up to eight willing creatures within range project your astral bodies into the Astral Plane. The material body you leave behind is unconscious and in a state of suspended animation; it doesn't need food or air and doesn't age. Your astral body resembles your mortal form in almost every way, replicating your game statistics and possessions. The principal difference is the addition of a silvery cord that extends from between your shoulder blades and trails behind you, fading to invisibility after 1 foot. This cord is your tether to your material body.", source: "SRD" },
    { name: "Foresight", level: 9, school: "divination", castingTime: "1 minute", range: "Touch", components: "V, S, M (a hummingbird feather)", duration: "8 hours", classes: ["Bard", "Druid", "Warlock", "Wizard"], description: "You touch a willing creature and bestow a limited ability to see into the immediate future. For the duration, the target can't be surprised and has advantage on attack rolls, ability checks, and saving throws. Additionally, other creatures have disadvantage on attack rolls against the target for the duration. This spell immediately ends if you cast it again before its duration ends.", source: "SRD" },
    { name: "Gate", level: 9, school: "conjuration", castingTime: "1 action", range: "60 feet", components: "V, S, M (a diamond worth at least 5,000 gp)", duration: "Concentration, up to 1 minute", classes: ["Cleric", "Sorcerer", "Wizard"], description: "You conjure a portal linking an unoccupied space you can see within range to a precise location on a different plane of existence. The portal is a circular opening, which you can make 5 to 20 feet in diameter. You can orient the portal in any direction you choose. The portal lasts for the duration. The portal has a front and a back on each plane where it appears. Travel through the portal is possible only by moving through its front. Anything that does so is instantly transported to the other plane, appearing in the unoccupied space nearest to the portal.", source: "SRD" },
    { name: "Imprisonment", level: 9, school: "abjuration", castingTime: "1 minute", range: "30 feet", components: "V, S, M (a vellum depiction or a carved statuette in the likeness of the target, and a special component that varies according to the version of the spell you choose, worth at least 500 gp per Hit Die of the target)", duration: "Until dispelled", classes: ["Warlock", "Wizard"], description: "You create a magical restraint to hold a creature that you can see within range. The target must succeed on a Wisdom saving throw or be bound by the spell; if it succeeds, it is immune to this spell if you cast it again. While affected by this spell, the creature doesn't need to breathe, eat, or drink, and it doesn't age. Divination spells can't locate or perceive the target. Choose one of the following forms of imprisonment: Burial, Chaining, Hedged Prison, Minimus Containment, or Slumber.", source: "SRD" }
];

if (typeof window !== 'undefined') {
    window.SRDSpells2 = SRDSpells2;
}
